window.storyFormat({"name":"Botscripten","description":"An interactive chat viewer","author":"Aibex, Inc","image":"icon.svg","url":"github:aibexhq/botscripten","version":"1.0.0","proofing":false,"source":"<!DOCTYPE html>\n<html>\n  <head>\n    <title>\n      {{STORY_NAME}}\n    </title>\n    <meta charset=\"utf-8\" />\n    <meta\n      name=\"viewport\"\n      content=\"width=device-width, initial-scale=1, shrink-to-fit=no\"\n    />\n  </head>\n  <body>\n    <twine>\n      {{STORY_DATA}}\n    </twine>\n    <section id=\"runtime\">\n      <nav>\n        <h1>{{STORY_NAME}}</h1>\n        <div id=\"toggles\">\n          <label\n            ><input\n              type=\"checkbox\"\n              name=\"show_directives\"\n              id=\"show_directives\"\n              checked=\"checked\"\n            />show directives</label\n          >\n          <label\n            ><input type=\"checkbox\" name=\"proofing\" id=\"proofing\" />show\n            proofing</label\n          >\n        </div>\n      </nav>\n\n      <main class=\"chat-panel\">\n        <div class=\"passages\">\n          <div id=\"phistory\" class=\"history chat-history\"></div>\n          <div id=\"passage\" class=\"active\"></div>\n          <div id=\"animation-container\">\n            <div class=\"chat-passage-wrapper\">\n              <div class=\"chat-passage\">\n                <div class=\"wave\">\n                  <span class=\"dot\"></span>\n                  <span class=\"dot\"></span>\n                  <span class=\"dot\"></span>\n                </div>\n              </div>\n            </div>\n          </div>\n        </div>\n        <div class=\"user-response-panel\">\n          <hr />\n          <div\n            id=\"user-response-hint\"\n            class=\"user-response-hint content-container\"\n          ></div>\n          <div id=\"user-response-panel\" class=\"user-reponse-wrapper\"></div>\n        </div>\n      </main>\n    </section>\n    <link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/gh/aibexhq/botscripten@1.0.0/src/template/botscripten.css\" /> <script src=\"https://cdn.jsdelivr.net/gh/aibexhq/botscripten@1.0.0/dist/botscripten.umd.js\"></script>\n  </body>\n</html>\n"})