window.storyFormat({"name":"BotscriptenViewer","description":"An interactive chat viewer for Botscripten","author":"Aibex, Inc","image":"icon.svg","url":"github:aibexhq/botscript","version":"0.4.0","proofing":false,"source":"<!DOCTYPE html>\n<html>\n  <head>\n    <title>\n      {{STORY_NAME}}\n    </title>\n    <meta charset=\"utf-8\" />\n    <meta\n      name=\"viewport\"\n      content=\"width=device-width, initial-scale=1, shrink-to-fit=no\"\n    />\n    <meta name=\"description\" content=\"\" />\n    <meta name=\"author\" content=\"\" />\n    <link rel=\"icon\" href=\"favicon.ico\" />\n\n    <!-- Bootstrap core CSS -->\n    <link\n      rel=\"stylesheet\"\n      href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css\"\n      integrity=\"sha384-WskhaSGFgHYWDcbwN70/dfYBj47jz9qbsMId/iRN3ewGhXQFZCSftd1LZCfmhktB\"\n      crossorigin=\"anonymous\"\n    />\n\n    <style>/* Variables */\n\n:root {\n  --navbar-height: 56px;\n  --user-response-panel-height: 110px;\n  --bg-color: #c7bdb5; /*global chat bg color*/\n  --user-color: #417505; /*user name & placeholder avatar bg*/\n  --speaker-color: #888; /*default color for speaker name & placeholder avatar bg*/\n  --sidebar-bg-color: #fff;\n  --navbar-bg-color: #fff;\n  --passage-bg-color: #fff;\n  --passage-text-color: #000;\n}\n\n/* Atomic Utilities */\n\n.bg-color {\n  background-color: var(--bg-color);\n}\n\n.minh-full {\n  min-height: calc(\n    100vh - var(--navbar-height) - var(--user-response-panel-height) - 17px -\n      5px\n  );\n}\n\n.ml--3 {\n  margin-left: -1rem !important;\n}\n\n@media (min-width: 992px) {\n  .w-lg-auto {\n    width: auto !important;\n  }\n}\n\n/* General */\n\nhtml,\nbody {\n  overflow-x: hidden; /* Prevent scroll on narrow devices */\n  position: relative;\n  min-height: 100%;\n}\n\nbody {\n  padding-top: var(--navbar-height);\n  margin-bottom: calc(\n    var(--user-response-panel-height) + 18px + 5px\n  ); /* + height of hint + hr */\n}\n\n/* Offcanvas sidebar */\n\n@media (max-width: 991.98px) {\n  .offcanvas-collapse {\n    z-index: 1100;\n    position: fixed;\n    top: var(--navbar-height); /* Height of navbar */\n    bottom: 0;\n    left: 100%;\n    width: 100%;\n    padding-right: 1rem;\n    padding-left: 1rem;\n    overflow-y: auto;\n    visibility: hidden;\n    background-color: var(--sidebar-bg-color);\n    transition-timing-function: ease-in-out;\n    transition-duration: 0.3s;\n    transition-property: left, visibility;\n  }\n  .offcanvas-collapse.open {\n    left: 0;\n    visibility: visible;\n  }\n}\n\n/* Nav */\n\nnav.navbar {\n  height: var(--navbar-height);\n  background-color: var(--navbar-bg-color);\n  padding: 0.5rem 15px;\n  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);\n}\n\nnav .nav-title {\n  font-size: 1.5rem;\n  line-height: 40px;\n  margin: 0 auto;\n}\n\nnav .nav-icon {\n  width: 35px;\n}\n\n/* Chat Panel */\n\n.chat-panel {\n  padding-top: 15px;\n}\n\n.chat-passage-wrapper {\n  position: relative;\n  margin: 0.5rem 0;\n}\n\n.chat-passage-wrapper[data-speaker=\"you\"] {\n  text-align: right;\n}\n.chat-passage-wrapper[data-speaker=\"you\"] .chat-passage:before {\n  text-align: right;\n  color: var(--user-color);\n}\n\n.chat-passage {\n  display: inline-block;\n  padding: 0.25rem 0.5rem;\n  margin-left: 35px;\n  background-color: var(--passage-bg-color);\n  max-width: calc(100% - 80px);\n  text-align: left;\n}\n@media (min-width: 576px) {\n  .chat-passage {\n    max-width: calc(100% - 100px);\n  }\n}\n@media (min-width: 768px) {\n  .chat-passage {\n    max-width: calc(100% - 80px);\n  }\n}\n@media (min-width: 992px) {\n  .chat-passage {\n    max-width: calc(100% - 100px);\n  }\n}\n\n.chat-passage:before {\n  content: attr(data-speaker);\n  display: block;\n  font-size: 0.75rem;\n  text-transform: capitalize;\n  color: var(--speaker-color);\n}\n\n.chat-passage p {\n  margin: 0;\n}\n\n.chat-passage img {\n  max-width: 100%;\n  max-height: calc(100vh - 2 * var(--user-response-panel-height));\n  padding: 0.25rem 0;\n}\n.chat-passage iframe {\n  max-width: 100%;\n  max-height: calc(100vh - 2 * var(--user-response-panel-height));\n}\n\n.chat-passage figure {\n  margin-bottom: 0.25rem;\n  font-size: 0.75rem;\n}\n\n/* User Response Panel */\n\n.user-response-panel {\n  padding-right: 15px;\n  padding-left: 15px;\n}\n@media (min-width: 992px) {\n  .user-response-panel {\n    max-width: calc(\n      100% / (12 / 6)\n    ); /* 100% / (12/[col width of chat-panel]) */\n    margin: 0 auto;\n  }\n}\n\n.user-response-panel hr {\n  margin: 0;\n  border-top: 5px solid white;\n}\n\n.user-response-hint {\n  font-style: italic;\n  font-size: 0.75rem;\n  text-align: center;\n  height: 18px;\n}\n@media (max-width: 379.98px) {\n  .user-response-hint {\n    font-size: 0.6rem;\n    height: 14px;\n  }\n  body {\n    margin-bottom: calc(\n      var(--user-response-panel-height) + 14px + 5px\n    ); /* + height of hint + hr */\n  }\n  .minh-full {\n    min-height: calc(\n      100vh - var(--navbar-height) - var(--user-response-panel-height) - 14px -\n        5px\n    );\n  }\n}\n@media (max-width: 319.98px) {\n  .user-response-hint {\n    font-size: 0.5rem;\n    height: 12px;\n  }\n  body {\n    margin-bottom: calc(\n      var(--user-response-panel-height) + 12px + 5px\n    ); /* + height of hint + hr */\n  }\n  .minh-full {\n    min-height: calc(\n      100vh - var(--navbar-height) - var(--user-response-panel-height) - 12px -\n        5px\n    );\n  }\n}\n\n.user-reponse-wrapper {\n  height: var(--user-response-panel-height);\n  display: flex;\n  align-items: center;\n  justify-content: center;\n}\n\n.user-response-panel .user-response {\n  display: inline-block;\n  padding: 0.5rem;\n  margin: 0.25rem;\n  background-color: var(--passage-bg-color);\n  color: var(--passage-text-color);\n  border-top: 2px solid transparent;\n  transition: border-color 0.15s ease-in;\n}\n.user-response-panel .user-response:hover {\n  text-decoration: none;\n  border-color: var(--user-color);\n}\n\n/* Passage Avatars */\n\n.chat-passage-wrapper:before {\n  margin-right: 5px;\n  position: absolute;\n  vertical-align: top;\n  content: \"\";\n  width: 30px;\n  height: 30px;\n  background-size: 30px 30px;\n  border-radius: 15px;\n  background-color: var(--speaker-color);\n}\n.chat-passage-wrapper[data-speaker=\"you\"]:before {\n  display: none;\n}\n.chat-passage-wrapper[data-speaker=\"you\"]:after {\n  margin-left: -30px;\n  position: absolute;\n  vertical-align: top;\n  content: \"\";\n  width: 30px;\n  height: 30px;\n  background-size: 30px 30px;\n  border-radius: 15px;\n  background-color: var(--user-color);\n}\n.chat-passage[data-speaker=\"you\"] {\n  margin-left: 0;\n  margin-right: 35px;\n}\n\n/* Typing Animation */\n\ndiv.wave {\n  position: relative;\n  text-align: center;\n  margin-left: auto;\n  margin-right: auto;\n}\ndiv.wave .dot {\n  display: inline-block;\n  width: 6px;\n  height: 6px;\n  border-radius: 50%;\n  margin-right: 2px;\n  background: var(--passage-text-color);\n  animation: wave 1.2s ease-in-out infinite;\n}\ndiv.wave .dot:nth-child(2) {\n  animation-delay: 0.1s;\n}\n\ndiv.wave .dot:nth-child(3) {\n  animation-delay: 0.2s;\n}\n\n@keyframes wave {\n  0%,\n  50%,\n  100% {\n    transform: initial;\n  }\n\n  25% {\n    transform: translateY(-15px);\n  }\n}\n\n/* Directives */\n.directives {\n  display: none;\n  font-family: Consolas, \"Andale Mono WT\", \"Andale Mono\", \"Lucida Console\",\n    \"Lucida Sans Typewriter\", \"DejaVu Sans Mono\", \"Bitstream Vera Sans Mono\",\n    \"Liberation Mono\", \"Nimbus Mono L\", Monaco, \"Courier New\", Courier,\n    monospace;\n  font-size: 12px;\n}\n\n.directive {\n  white-space: pre;\n  border: 1px dashed #666;\n  position: relative;\n}\n\n.directive::after {\n  content: attr(name);\n  position: absolute;\n  right: 2px;\n  top: 2px;\n  font-weight: bold;\n  font-size: 14px;\n  letter-spacing: 0.2em;\n}\n\nbody.show-directives .directives {\n  display: block;\n}\n\n/* \n * counter extra padding-right added by BS4 modal\n * https://stackoverflow.com/questions/32862394/bootstrap-modals-keep-adding-padding-right-to-body-after-closed\n */\nbody.modal-open {\n  padding-right: 0 !important;\n}\nbody.modal-open .user-response-panel {\n  padding-right: 15px !important;\n}\nbody.modal-open nav.navbar {\n  padding-right: 15px !important;\n}\n</style>\n  </head>\n  <body class=\"bg-white\">\n    <nav class=\"navbar navbar-expand-lg fixed-top\">\n      <h1 id=\"ptitle\" class=\"nav-title\">{{STORY_NAME}}</h1>\n      <label\n        ><input\n          type=\"checkbox\"\n          name=\"show_directives\"\n          id=\"show_directives\"\n          checked=\"checked\"\n        />show directives</label\n      >\n    </nav>\n\n    <main role=\"main\" class=\"container-fluid\">\n      <div class=\"row\">\n        <div class=\"left-sidebar col-lg d-none d-lg-block\">\n          <div\n            id=\"left-sidebar-container\"\n            class=\"content-container position-fixed mt-3 mt-lg-5 m-lg-4\"\n          ></div>\n        </div>\n        <div class=\"chat-panel col-lg-6 minh-full bg-color\">\n          <div id=\"phistory\" class=\"history chat-history\"></div>\n          <div id=\"passage\" class=\"active\"></div>\n          <div id=\"animation-container\">\n            <div class=\"chat-passage-wrapper\">\n              <div class=\"chat-passage\">\n                <div class=\"wave\">\n                  <span class=\"dot\"></span>\n                  <span class=\"dot\"></span>\n                  <span class=\"dot\"></span>\n                </div>\n              </div>\n            </div>\n          </div>\n          <div class=\"user-response-panel fixed-bottom bg-color\">\n            <hr />\n            <div\n              id=\"user-response-hint\"\n              class=\"user-response-hint content-container\"\n            ></div>\n            <div id=\"user-response-panel\" class=\"user-reponse-wrapper\"></div>\n          </div>\n        </div>\n        <div class=\"right-sidebar col-lg offcanvas-collapse\">\n          <div class=\"position-fixed ml--3 w-100 w-lg-auto h-100\">\n            <div\n              id=\"right-sidebar-container\"\n              class=\"content-container m-3 m-sm-5 m-lg-3 mt-lg-5 m-xl-5 mt-xl-5\"\n            ></div>\n          </div>\n        </div>\n      </div>\n    </main>\n    {{STORY_DATA}} \n  </body>\n</html>\n"})